document.addEventListener('DOMContentLoaded',function(){const video=document.getElementById('arteamo-video');const playButton=document.querySelector('.play-button');const videoOverlay=document.querySelector('.video-overlay');const playPauseBtn=document.querySelector('.play-pause');const playIcon=document.querySelector('.play-icon');const pauseIcon=document.querySelector('.pause-icon');const soundToggle=document.querySelector('.sound-toggle');const soundOn=document.querySelector('.sound-on');const soundOff=document.querySelector('.sound-off');const progressBar=document.querySelector('.progress-bar');const progressFill=document.querySelector('.progress-fill');const videoControls=document.querySelector('.video-controls');if(!video)return;playButton.addEventListener('click',function(){video.play();videoOverlay.classList.add('hidden');videoControls.classList.add('visible');updatePlayPauseButton(true);});playPauseBtn.addEventListener('click',function(){if(video.paused){video.play();updatePlayPauseButton(true);}else{video.pause();updatePlayPauseButton(false);}});soundToggle.addEventListener('click',function(){video.muted=!video.muted;updateSoundButton(video.muted);});progressBar.addEventListener('click',function(e){const rect=progressBar.getBoundingClientRect();const percent=(e.clientX-rect.left)/rect.width;video.currentTime=percent*video.duration;});video.addEventListener('timeupdate',function(){const percent=(video.currentTime/video.duration)*100;progressFill.style.width=percent+'%';});video.addEventListener('ended',function(){videoOverlay.classList.remove('hidden');updatePlayPauseButton(false);progressFill.style.width='0%';});let controlsTimeout;video.addEventListener('play',function(){clearTimeout(controlsTimeout);controlsTimeout=setTimeout(function(){if(!video.paused){videoControls.classList.remove('visible');}},3000);});video.addEventListener('pause',function(){clearTimeout(controlsTimeout);videoControls.classList.add('visible');});const videoContainer=document.querySelector('.video-container');videoContainer.addEventListener('mousemove',function(){videoControls.classList.add('visible');clearTimeout(controlsTimeout);if(!video.paused){controlsTimeout=setTimeout(function(){videoControls.classList.remove('visible');},3000);}});function updatePlayPauseButton(isPlaying){if(isPlaying){playIcon.style.display='none';pauseIcon.style.display='inline';}else{playIcon.style.display='inline';pauseIcon.style.display='none';}}function updateSoundButton(isMuted){if(isMuted){soundOn.style.display='none';soundOff.style.display='inline';}else{soundOn.style.display='inline';soundOff.style.display='none';}}updatePlayPauseButton(false);updateSoundButton(video.muted);});